$(document).ready(function(){var t=$("meta[name=csrf-token]").attr("content");$("body").on("click","span.glyphicon-cog",function(){var e=$(this).attr("id"),n=parseInt(e.substring(14,e.length));$.ajax({url:"/post_settings_modal",headers:{Accept:"text/javascript; charset=utf-8","Content-Type":"application/x-www-form-urlencoded; charset=UTF-8"},type:"GET",data:{authenticity_token:t,post_id:n}})}),$("body").on("click",".modal-content",function(t){t.stopPropagation()}),$(document).on("click",function(){$(".post-settings-modal").remove(),$(".page-overlay").css("display","none")}),$("body").on("click",".post-settings-modal .post-settings-delete",function(){if(window.confirm("Are you sure?")){var e=$(this).attr("id"),n=parseInt(e.substring(12,e.length));$.ajax({url:"/posts/"+n,headers:{Accept:"text/html; charset=utf-8","Content-Type":"application/x-www-form-urlencoded; charset=UTF-8"},type:"DELETE",data:{authenticity_token:t,post_id:n}})}})});